(()=>{"use strict";class e{constructor(e,t){this.categoria=e,this.frase=t}}document.addEventListener("DOMContentLoaded",(function(){const t=document.getElementById("fraseAleatoriaDiv"),o=JSON.parse(localStorage.getItem("fraseAleatoria"));t.innerHTML=o.frase;const r=JSON.parse(localStorage.getItem("categoria")),a=document.getElementById("frase"),n=document.getElementById("categoriaSelec");fetch(`https://api.chucknorris.io/jokes/random?category=${r.nombre}`).then((e=>e.json())).then((t=>{const o=new e(r,t.value);localStorage.setItem("frase",JSON.stringify(o)),n.textContent=r.nombre,a.textContent=o.frase})).catch((e=>{console.error("Error al obtener la frase: "+e)}))}))})();